    68→
    69→// Progress Ring Component
    70→function ProgressRing({
    71→  progress,
    72→  size = 200,
    73→  strokeWidth = 8,
    74→  children,
    75→}: {
    76→  progress: number;
    77→  size?: number;
    78→  strokeWidth?: number;
    79→  children?: React.ReactNode;
    80→}) {
    81→  const radius = (size - strokeWidth) / 2;
    82→  const circumference = radius * 2 * Math.PI;
    83→  const strokeDashoffset = circumference - (Math.min(progress, 100) / 100) * circumference;
    84→
    85→  return (
    86→    <div className="relative inline-flex items-center justify-center">
    87→      <svg width={size} height={size} className="-rotate-90">
    88→        {/* Background circle */}
    89→        <circle
    90→          cx={size / 2}
    91→          cy={size / 2}
    92→          r={radius}
    93→          fill="none"
    94→          stroke="hsl(var(--muted))"
    95→          strokeWidth={strokeWidth}
    96→        />
    97→        {/* Progress circle */}
    98→        <circle
    99→          cx={size / 2}
   100→          cy={size / 2}
   101→          r={radius}
   102→          fill="none"
   103→          stroke="hsl(var(--primary))"
   104→          strokeWidth={strokeWidth}
   105→          strokeLinecap="round"
   106→          strokeDasharray={circumference}
   107→          strokeDashoffset={strokeDashoffset}
   108→          className="transition-all duration-1000 ease-out"
   109→          style={{
   110→            filter: progress >= 100 ? 'drop-shadow(0 0 6px hsl(var(--primary)))' : undefined,
   111→          }}
   112→        />
   113→      </svg>
   114→      <div className="absolute inset-0 flex flex-col items-center justify-center">
   115→        {children}
   116→      </div>
   117→    </div>
   118→  );
   119→}
   120→
   121→// Loading skeleton for the dashboard
   122→function DashboardSkeleton() {
   123→  return (
   124→    <div className="min-h-screen bg-background p-4 md:p-6">
   125→      {/* Header */}
   126→      <div className="flex items-center justify-between mb-6">
   127→        <Skeleton className="h-8 w-40" />
   128→        <Skeleton className="h-10 w-36" />
   129→      </div>
   130→
   131→      {/* Hero section */}
   132→      <div className="flex flex-col items-center mb-8">
   133→        <Skeleton className="h-[200px] w-[200px] rounded-full" />
   134→        <Skeleton className="h-4 w-24 mt-4" />
   135→      </div>
   136→
   137→      {/* Quick stats */}
   138→      <div className="flex gap-4 mb-8 overflow-x-auto pb-2">
   139→        {[1, 2, 3].map((i) => (
