   154→}
   155→
   156→// Empty state component
   157→function EmptyState({
   158→  title,
   159→  description,
   160→  action,
   161→}: {
   162→  title: string;
   163→  description: string;
   164→  action?: React.ReactNode;
   165→}) {
   166→  return (
   167→    <div className="flex flex-col items-center justify-center py-12 text-center">
   168→      <div className="rounded-full bg-muted p-4 mb-4">
   169→        <Dumbbell className="h-8 w-8 text-muted-foreground" />
   170→      </div>
   171→      <h3 className="text-lg font-semibold mb-2">{title}</h3>
   172→      <p className="text-muted-foreground text-sm max-w-sm mb-4">{description}</p>
