   176→}
   177→
   178→// Add Training Form
   179→function AddTrainingForm({
   180→  uebungen,
   181→  onSuccess,
   182→  onClose,
   183→}: {
   184→  uebungen: Uebungen[];
   185→  onSuccess: () => void;
   186→  onClose: () => void;
   187→}) {
   188→  const [submitting, setSubmitting] = useState(false);
   189→  const [formData, setFormData] = useState({
   190→    trainingsdatum: format(new Date(), 'yyyy-MM-dd'),
   191→    trainingsdauer: '',
   192→    intensitaet: '',
   193→    kalorien: '',
   194→    stimmung: '',
   195→    notizen_training: '',
   196→  });
   197→
   198→  async function handleSubmit(e: React.FormEvent) {
   199→    e.preventDefault();
   200→    setSubmitting(true);
   201→
   202→    try {
   203→      const now = new Date();
   204→      const timeString = `${String(now.getHours()).padStart(2, '0')}:${String(now.getMinutes()).padStart(2, '0')}`;
   205→
   206→      await LivingAppsService.createTrainingseinheitenEntry({
   207→        trainingsdatum: `${formData.trainingsdatum}T${timeString}`,
   208→        trainingsdauer: formData.trainingsdauer ? Number(formData.trainingsdauer) : undefined,
   209→        intensitaet: formData.intensitaet as Trainingseinheiten['fields']['intensitaet'],
   210→        kalorien: formData.kalorien ? Number(formData.kalorien) : undefined,
   211→        stimmung: formData.stimmung as Trainingseinheiten['fields']['stimmung'],
   212→        notizen_training: formData.notizen_training || undefined,
   213→      });
   214→      onSuccess();
   215→      onClose();
   216→    } catch (err) {
   217→      console.error('Failed to create training:', err);
   218→    } finally {
   219→      setSubmitting(false);
   220→    }
   221→  }
   222→
   223→  return (
   224→    <form onSubmit={handleSubmit} className="space-y-4">
   225→      <div className="space-y-2">
   226→        <Label htmlFor="trainingsdatum">Datum</Label>
   227→        <Input
   228→          id="trainingsdatum"
   229→          type="date"
   230→          value={formData.trainingsdatum}
   231→          onChange={(e) => setFormData({ ...formData, trainingsdatum: e.target.value })}
   232→          required
   233→        />
   234→      </div>
   235→
   236→      <div className="grid grid-cols-2 gap-4">
   237→        <div className="space-y-2">
   238→          <Label htmlFor="trainingsdauer">Dauer (Minuten)</Label>
   239→          <Input
   240→            id="trainingsdauer"
   241→            type="number"
   242→            placeholder="z.B. 45"
   243→            value={formData.trainingsdauer}
   244→            onChange={(e) => setFormData({ ...formData, trainingsdauer: e.target.value })}
   245→          />
   246→        </div>
   247→
   248→        <div className="space-y-2">
   249→          <Label htmlFor="kalorien">Kalorien (kcal)</Label>
   250→          <Input
   251→            id="kalorien"
   252→            type="number"
   253→            placeholder="z.B. 350"
   254→            value={formData.kalorien}
   255→            onChange={(e) => setFormData({ ...formData, kalorien: e.target.value })}
   256→          />
   257→        </div>
   258→      </div>
   259→
   260→      <div className="space-y-2">
   261→        <Label htmlFor="intensitaet">Intensität</Label>
   262→        <Select
   263→          value={formData.intensitaet || undefined}
   264→          onValueChange={(value) => setFormData({ ...formData, intensitaet: value })}
   265→        >
   266→          <SelectTrigger className="w-full">
   267→            <SelectValue placeholder="Intensität wählen..." />
   268→          </SelectTrigger>
   269→          <SelectContent>
   270→            <SelectItem value="sehr_leicht">Sehr leicht</SelectItem>
   271→            <SelectItem value="leicht">Leicht</SelectItem>
   272→            <SelectItem value="mittel">Mittel</SelectItem>
   273→            <SelectItem value="hoch">Hoch</SelectItem>
   274→            <SelectItem value="sehr_hoch">Sehr hoch</SelectItem>
   275→            <SelectItem value="maximal">Maximal</SelectItem>
   276→          </SelectContent>
   277→        </Select>
   278→      </div>
   279→
   280→      <div className="space-y-2">
   281→        <Label htmlFor="stimmung">Stimmung/Energielevel</Label>
   282→        <Select
   283→          value={formData.stimmung || undefined}
   284→          onValueChange={(value) => setFormData({ ...formData, stimmung: value })}
   285→        >
   286→          <SelectTrigger className="w-full">
   287→            <SelectValue placeholder="Stimmung wählen..." />
   288→          </SelectTrigger>
   289→          <SelectContent>
   290→            <SelectItem value="sehr_schlecht">Sehr schlecht</SelectItem>
   291→            <SelectItem value="schlecht">Schlecht</SelectItem>
   292→            <SelectItem value="neutral">Neutral</SelectItem>
   293→            <SelectItem value="gut">Gut</SelectItem>
   294→            <SelectItem value="sehr_gut">Sehr gut</SelectItem>
   295→            <SelectItem value="ausgezeichnet">Ausgezeichnet</SelectItem>
   296→          </SelectContent>
   297→        </Select>
   298→      </div>
   299→
   300→      <div className="space-y-2">
   301→        <Label htmlFor="notizen">Notizen</Label>
   302→        <Textarea
   303→          id="notizen"
   304→          placeholder="Wie war das Training?"
   305→          value={formData.notizen_training}
   306→          onChange={(e) => setFormData({ ...formData, notizen_training: e.target.value })}
   307→          rows={3}
   308→        />
   309→      </div>
   310→
   311→      <DialogFooter className="gap-2 sm:gap-0">
   312→        <DialogClose asChild>
   313→          <Button type="button" variant="outline">
   314→            Abbrechen
   315→          </Button>
   316→        </DialogClose>
   317→        <Button type="submit" disabled={submitting}>
   318→          {submitting ? 'Speichern...' : 'Training speichern'}
   319→        </Button>
   320→      </DialogFooter>
   321→    </form>
   322→  );
   323→}
   324→
   325→export default function Dashboard() {
   326→  const [trainingseinheiten, setTrainingseinheiten] = useState<Trainingseinheiten[]>([]);
   327→  const [koerpermessungen, setKoerpermessungen] = useState<Koerpermessungen[]>([]);
   328→  const [trainingsziele, setTrainingsziele] = useState<Trainingsziele[]>([]);
   329→  const [uebungen, setUebungen] = useState<Uebungen[]>([]);
   330→  const [loading, setLoading] = useState(true);
   331→  const [error, setError] = useState<Error | null>(null);
   332→  const [dialogOpen, setDialogOpen] = useState(false);
   333→
   334→  async function loadData() {
   335→    try {
   336→      setLoading(true);
   337→      setError(null);
   338→      const [t, k, z, u] = await Promise.all([
   339→        LivingAppsService.getTrainingseinheiten(),
   340→        LivingAppsService.getKoerpermessungen(),
   341→        LivingAppsService.getTrainingsziele(),
   342→        LivingAppsService.getUebungen(),
   343→      ]);
   344→      setTrainingseinheiten(t);
   345→      setKoerpermessungen(k);
   346→      setTrainingsziele(z);
   347→      setUebungen(u);
   348→    } catch (err) {
   349→      setError(err instanceof Error ? err : new Error('Fehler beim Laden der Daten'));
   350→    } finally {
   351→      setLoading(false);
   352→    }
   353→  }
   354→
   355→  useEffect(() => {
   356→    loadData();
   357→  }, []);
   358→
   359→  // Calculate KPIs
   360→  const kpis = useMemo(() => {
   361→    const now = new Date();
   362→    const weekStart = startOfWeek(now, { weekStartsOn: 1 }); // Monday
   363→    const weekEnd = endOfWeek(now, { weekStartsOn: 1 });
   364→
   365→    // Workouts this week
   366→    const workoutsThisWeek = trainingseinheiten.filter((t) => {
   367→      if (!t.fields.trainingsdatum) return false;
   368→      const date = parseISO(t.fields.trainingsdatum);
   369→      return isWithinInterval(date, { start: weekStart, end: weekEnd });
   370→    });
   371→
   372→    // Weekly goal (default 4)
   373→    const weeklyGoal = 4;
   374→    const workoutCount = workoutsThisWeek.length;
   375→    const progressPercent = (workoutCount / weeklyGoal) * 100;
   376→
   377→    // Latest weight
   378→    const sortedMeasurements = [...koerpermessungen].sort((a, b) => {
   379→      const dateA = a.fields.messdatum || a.createdat;
   380→      const dateB = b.fields.messdatum || b.createdat;
   381→      return dateB.localeCompare(dateA);
   382→    });
   383→    const latestWeight = sortedMeasurements[0]?.fields.gewicht;
   384→
   385→    // Training streak
   386→    let streak = 0;
   387→    const sortedWorkouts = [...trainingseinheiten].sort((a, b) => {
   388→      const dateA = a.fields.trainingsdatum || a.createdat;
   389→      const dateB = b.fields.trainingsdatum || b.createdat;
   390→      return dateB.localeCompare(dateA);
   391→    });
   392→
   393→    if (sortedWorkouts.length > 0) {
   394→      const workoutDates = new Set(
   395→        sortedWorkouts.map((w) => {
   396→          const d = w.fields.trainingsdatum || w.createdat;
   397→          return d.split('T')[0];
   398→        })
   399→      );
   400→
   401→      let checkDate = new Date();
   402→      // Check if today has workout, if not check yesterday
   403→      let todayStr = format(checkDate, 'yyyy-MM-dd');
   404→      if (!workoutDates.has(todayStr)) {
   405→        checkDate = subDays(checkDate, 1);
   406→        todayStr = format(checkDate, 'yyyy-MM-dd');
   407→        if (!workoutDates.has(todayStr)) {
   408→          streak = 0;
   409→        } else {
   410→          streak = 1;
   411→          checkDate = subDays(checkDate, 1);
   412→        }
   413→      } else {
   414→        streak = 1;
   415→        checkDate = subDays(checkDate, 1);
   416→      }
   417→
   418→      if (streak > 0) {
   419→        while (workoutDates.has(format(checkDate, 'yyyy-MM-dd'))) {
   420→          streak++;
   421→          checkDate = subDays(checkDate, 1);
   422→        }
   423→      }
   424→    }
   425→
   426→    // Calories this week
   427→    const caloriesThisWeek = workoutsThisWeek.reduce(
   428→      (sum, w) => sum + (w.fields.kalorien || 0),
   429→      0
   430→    );
   431→
   432→    return {
   433→      workoutCount,
   434→      weeklyGoal,
   435→      progressPercent,
   436→      latestWeight,
   437→      streak,
   438→      caloriesThisWeek,
   439→    };
   440→  }, [trainingseinheiten, koerpermessungen]);
   441→
   442→  // Weight chart data (last 30 days)
   443→  const weightChartData = useMemo(() => {
   444→    const last30Days = subDays(new Date(), 30);
   445→    return koerpermessungen
   446→      .filter((m) => {
   447→        const date = m.fields.messdatum || m.createdat;
   448→        return parseISO(date) >= last30Days && m.fields.gewicht != null;
   449→      })
   450→      .sort((a, b) => {
   451→        const dateA = a.fields.messdatum || a.createdat;
   452→        const dateB = b.fields.messdatum || b.createdat;
   453→        return dateA.localeCompare(dateB);
   454→      })
   455→      .map((m) => ({
   456→        date: format(parseISO(m.fields.messdatum || m.createdat), 'dd. MMM', { locale: de }),
   457→        gewicht: m.fields.gewicht,
   458→      }));
   459→  }, [koerpermessungen]);
   460→
   461→  // Recent workouts (last 5)
   462→  const recentWorkouts = useMemo(() => {
   463→    return [...trainingseinheiten]
   464→      .sort((a, b) => {
   465→        const dateA = a.fields.trainingsdatum || a.createdat;
   466→        const dateB = b.fields.trainingsdatum || b.createdat;
   467→        return dateB.localeCompare(dateA);
   468→      })
   469→      .slice(0, 5);
   470→  }, [trainingseinheiten]);
   471→
   472→  // Active goals
   473→  const activeGoals = useMemo(() => {
   474→    return trainingsziele
   475→      .filter(
   476→        (z) =>
   477→          z.fields.aktueller_status === 'in_arbeit' ||
   478→          z.fields.aktueller_status === 'nicht_begonnen'
   479→      )
