   483→        return dateA.localeCompare(dateB);
   484→      })
   485→      .slice(0, 3);
   486→  }, [trainingsziele]);
   487→
   488→  if (loading) {
   489→    return <DashboardSkeleton />;
   490→  }
   491→
   492→  if (error) {
   493→    return (
   494→      <div className="min-h-screen bg-background p-4 flex items-center justify-center">
