import { useState, useEffect, useMemo } from 'react';
import type { Trainingseinheiten, Koerpermessungen, Trainingsziele, Uebungen } from '@/types/app';
import { LivingAppsService } from '@/services/livingAppsService';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Skeleton } from '@/components/ui/skeleton';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
  DialogClose,
} from '@/components/ui/dialog';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { AreaChart, Area, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';
import { format, parseISO, startOfWeek, endOfWeek, isWithinInterval, subDays, differenceInCalendarDays } from 'date-fns';
import { de } from 'date-fns/locale';
import { Plus, Scale, Flame, Trophy, Clock, Dumbbell, Calendar } from 'lucide-react';

// Intensity display labels
const INTENSITY_LABELS: Record<string, string> = {
  sehr_leicht: 'Sehr leicht',
  leicht: 'Leicht',
