import { useState, useEffect, useMemo } from 'react';
import type { Trainingseinheiten, Koerpermessungen, Trainingsziele } from '@/types/app';
import { LivingAppsService } from '@/services/livingAppsService';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Skeleton } from '@/components/ui/skeleton';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
  DialogClose,
} from '@/components/ui/dialog';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { AreaChart, Area, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';
import { format, parseISO, startOfWeek, endOfWeek, isWithinInterval, subDays } from 'date-fns';
import { de } from 'date-fns/locale';
import { Plus, Scale, Flame, Trophy, Clock, Dumbbell, Calendar } from 'lucide-react';

// Intensity display labels
const INTENSITY_LABELS: Record<string, string> = {
  sehr_leicht: 'Sehr leicht',
  leicht: 'Leicht',
  mittel: 'Mittel',
  hoch: 'Hoch',
  sehr_hoch: 'Sehr hoch',
  maximal: 'Maximal',
};

const STIMMUNG_LABELS: Record<string, string> = {
  sehr_schlecht: 'Sehr schlecht',
  schlecht: 'Schlecht',
  neutral: 'Neutral',
  gut: 'Gut',
  sehr_gut: 'Sehr gut',
  ausgezeichnet: 'Ausgezeichnet',
};

const GOAL_STATUS_LABELS: Record<string, string> = {
  nicht_begonnen: 'Nicht begonnen',
  in_arbeit: 'In Arbeit',
  erreicht: 'Erreicht',
  pausiert: 'Pausiert',
  aufgegeben: 'Aufgegeben',
};

const GOAL_CATEGORY_LABELS: Record<string, string> = {
  gewichtsabnahme: 'Gewichtsabnahme',
  muskelaufbau: 'Muskelaufbau',
  ausdauer: 'Ausdauer',
  kraft: 'Kraft',
  flexibilitaet: 'Flexibilität',
  allgemeine_fitness: 'Allgemeine Fitness',
  sonstiges: 'Sonstiges',
};

// Progress Ring Component
function ProgressRing({
  progress,
  size = 200,
  strokeWidth = 8,
  children,
}: {
  progress: number;
  size?: number;
  strokeWidth?: number;
  children?: React.ReactNode;
}) {
  const radius = (size - strokeWidth) / 2;
  const circumference = radius * 2 * Math.PI;
  const strokeDashoffset = circumference - (Math.min(progress, 100) / 100) * circumference;

  return (
    <div className="relative inline-flex items-center justify-center">
      <svg width={size} height={size} className="-rotate-90">
        {/* Background circle */}
        <circle
          cx={size / 2}
          cy={size / 2}
          r={radius}
          fill="none"
          stroke="hsl(var(--muted))"
          strokeWidth={strokeWidth}
        />
        {/* Progress circle */}
        <circle
          cx={size / 2}
          cy={size / 2}
          r={radius}
          fill="none"
          stroke="hsl(var(--primary))"
          strokeWidth={strokeWidth}
          strokeLinecap="round"
          strokeDasharray={circumference}
          strokeDashoffset={strokeDashoffset}
          className="transition-all duration-1000 ease-out"
          style={{
            filter: progress >= 100 ? 'drop-shadow(0 0 6px hsl(var(--primary)))' : undefined,
          }}
        />
      </svg>
      <div className="absolute inset-0 flex flex-col items-center justify-center">
        {children}
      </div>
    </div>
  );
}

// Loading skeleton for the dashboard
function DashboardSkeleton() {
  return (
    <div className="min-h-screen bg-background p-4 md:p-6">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <Skeleton className="h-8 w-40" />
        <Skeleton className="h-10 w-36" />
      </div>

      {/* Hero section */}
      <div className="flex flex-col items-center mb-8">
        <Skeleton className="h-[200px] w-[200px] rounded-full" />
        <Skeleton className="h-4 w-24 mt-4" />
      </div>

      {/* Quick stats */}
      <div className="flex gap-4 mb-8 overflow-x-auto pb-2">
        {[1, 2, 3].map((i) => (
          <Skeleton key={i} className="h-20 w-28 flex-shrink-0 rounded-lg" />
        ))}
      </div>

      {/* Chart */}
      <Skeleton className="h-[200px] w-full rounded-lg mb-8" />

      {/* Recent workouts */}
      <Skeleton className="h-6 w-32 mb-4" />
      {[1, 2, 3].map((i) => (
        <Skeleton key={i} className="h-20 w-full rounded-lg mb-3" />
      ))}
    </div>
