              <span className="text-2xl">⚠️</span>
            </div>
            <h2 className="text-lg font-semibold mb-2">Fehler beim Laden</h2>
            <p className="text-muted-foreground text-sm mb-4">{error.message}</p>
            <Button onClick={loadData}>Erneut versuchen</Button>
          </CardContent>
        </Card>
      </div>
    );
  }

